import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Add this interface to define the shape of a report item
interface ReportItem {
  id: number;
  name: string;
  grade: string;
  vaccinated: boolean;
}

const ReportPage = () => {
  // Update the useState to use the ReportItem type
  const [report, setReport] = useState<ReportItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get<ReportItem[]>('http://localhost:8080/api/report')
      .then(res => {
        setReport(res.data);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading report...</div>;

  return (
    <div className="report-container">
      <h2 style={{ textAlign: "center" }}>Vaccination Report</h2>
      <table className="report-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Grade</th>
            <th>Vaccinated</th>
          </tr>
        </thead>
        <tbody>
          {report.map(student => (
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>{student.name}</td>
              <td>{student.grade}</td>
              <td>{student.vaccinated ? 'Yes' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReportPage;
