import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography
} from '@mui/material';

// Define the type for a report item
interface ReportItem {
  id: number;
  name: string;
  grade: string;
  vaccinated: boolean;
}

const ReportPage: React.FC = () => {
  const [report, setReport] = useState<ReportItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get<ReportItem[]>('http://localhost:8080/api/report')
      .then(res => {
        setReport(res.data);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading report...</div>;

  return (
    <div style={{ maxWidth: 900, margin: "40px auto" }}>
      <Typography variant="h4" align="center" gutterBottom>
        Vaccination Report
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><b>ID</b></TableCell>
              <TableCell><b>Name</b></TableCell>
              <TableCell><b>Grade</b></TableCell>
              <TableCell><b>Vaccinated</b></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {report.map(student => (
              <TableRow key={student.id}>
                <TableCell align="center">{student.id}</TableCell>
                <TableCell>{student.name}</TableCell>
                <TableCell align="center">{student.grade}</TableCell>
                <TableCell align="center">{student.vaccinated ? 'Yes' : 'No'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default ReportPage;
