import React, { useEffect, useState } from 'react';
import axios from 'axios';

// Add this interface to define the shape of a report item
interface ReportItem {
  id: number;
  name: string;
  grade: string;
  vaccinated: boolean;
}

const ReportPage = () => {
  // Update the useState to use the ReportItem type
  const [report, setReport] = useState<ReportItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get<ReportItem[]>('http://localhost:8080/api/report')
      .then(res => {
        setReport(res.data);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading report...</div>;

  return (
    <div style={{ maxWidth: 800, margin: "40px auto" }}>
      <h2 style={{ textAlign: "center" }}>Vaccination Report</h2>
      <table style={{
        width: "100%",
        borderCollapse: "collapse",
        margin: "0 auto",
        background: "#fff",
        boxShadow: "0 2px 8px rgba(0,0,0,0.05)"
      }}>
        <thead>
          <tr>
            <th style={{ border: "1px solid #ddd", padding: "10px", background: "#f5f5f5" }}>ID</th>
            <th style={{ border: "1px solid #ddd", padding: "10px", background: "#f5f5f5" }}>Name</th>
            <th style={{ border: "1px solid #ddd", padding: "10px", background: "#f5f5f5" }}>Grade</th>
            <th style={{ border: "1px solid #ddd", padding: "10px", background: "#f5f5f5" }}>Vaccinated</th>
          </tr>
        </thead>
        <tbody>
          {report.map(student => (
            <tr key={student.id}>
              <td style={{ border: "1px solid #ddd", padding: "8px", textAlign: "center" }}>{student.id}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px" }}>{student.name}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px", textAlign: "center" }}>{student.grade}</td>
              <td style={{ border: "1px solid #ddd", padding: "8px", textAlign: "center" }}>
                {student.vaccinated ? 'Yes' : 'No'}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReportPage;
